import asyncio

from random import choice

from pyrogram.types import Message
from pyrogram import filters, Client

from config import SUDO_USERS
from STORMDB.data import GROUP, PORM


@Client.on_message(
    filters.command(["spam"], ".") & (filters.me | filters.user(SUDO_USERS))
)
async def spam(client: Client, message: Message):
    kex = message.text.split(" ", 2)

    if  len(kex) == 3:
        quantity, spam_text = int(kex[1]), kex[2]

        if message.reply_to_message:
            id = message.reply_to_message_id
            for _ in range(quantity):
                await message.reply_text(spam_text, reply_to_message_id=id)
        else:
            cid = message.chat.id
            for _ in range(quantity):
                await client.send_message(cid, spam_text)

    elif message.reply_to_message:
        spam_text = message.reply_to_message.text
        quantity = int(kex[1])
        cid = message.chat.id
        for _ in range(quantity):
            await client.send_message(cid, spam_text)
    else:
        await message.reply_text(f"!ꜱᴘᴀᴍ 13 <ʀᴇᴘʟʏ ᴛᴏ ᴛᴇxᴛ> <ʏᴏᴜʀ ᴛᴇxᴛ> <ʀᴇᴘʟʏ ᴛᴏ ᴜꜱᴇʀ>")


@Client.on_message(
    filters.command(["pspam", "pornspam"], ".") & (filters.me | filters.user(SUDO_USERS))
)
async def pspam(client: Client, message: Message):
    cid = message.chat.id
    if int(cid) in GROUP:
        await message.reply_text("ꜱʀʏ, ᴛʜɪꜱ ɢʀᴘ ɪꜱ ᴘʀᴏᴛᴇᴄᴛᴇᴅ ʙʏ ꜱᴛᴏʀᴍ 🛡️")
        return

    altp = message.text.split(" ", 2)
    if len(altp) > 1:
        quantity = int(altp[1])
        for _ in range(quantity):
            porm = choice(PORM)
            await client.send_video(cid, porm)
    else:
        await message.reply_text(f"!ᴘꜱᴘᴀᴍ 13")


@Client.on_message(
    filters.command(["hang"], ".") & (filters.me | filters.user(SUDO_USERS))
)
async def hang(xspam: Client, message: Message): 
    kex = message.text.split(" ")
    if len(kex) == 1:
        await message.reply_text(f"!ʜᴀɴɢ 10")
